import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  email: string = "";
  password: string = "";
  private apiUrl = 'http://localhost:3000/users/register';
  constructor(private http: HttpClient) {}


  userLogin(myPassword: string) {
    this.http.post(this.apiUrl, { email: this.email, password: myPassword }).subscribe((data: any) =>{
      console.log(data);
    })
  }
  /*
  userLogin(myEmail: string, myPassword: string) {
    this.http.post(this.apiUrl, { email: myEmail, password: myPassword }).subscribe((data: any) =>{
      console.log(data);
    })
  }
  */
}
