import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {  
  private apiUrl = 'http://localhost:3000/';
  constructor(private http: HttpClient) {}

  // POST on 'Add Item' click
  addItem(itemName: any, priceCost: any, brand: any)  {
    if (!itemName) {
      alert("Input item name!");
    } if (!priceCost) {
      alert("Input price!");
    } else {
      this.http.post(this.apiUrl, { name: itemName, price: priceCost }).subscribe();        // { name: item } because model requires name, so set the json of name to item
      alert("Name: " + itemName + ". Price: " + priceCost);
    }
  }
  /*
  Need to include validation for if priceCost to be numeric.
  Otherwise app breaks.
  */

}
